mapper.select:
select b.business_id from sys_group g 
inner join sys_business_group s on g.id=s.group_id 
inner join sys_business b on s.business_id=b.id  where g.id = #{id}

    select s.id, s.business_id, s.business_name
     from sys_group g 
     inner join sys_business_group b on g.id = b.group_id
     inner join sys_business s on s.id=b.business_id

	 b.group_id  = #{groupId,jdbcType=INTEGER}
	 and g.game_type =#{gameType}

select s.business_id from tb_user u 
inner join sys_business s on s.phone = u.u_account_name
inner join sys_business_group b on s.id=b.business_id
inner join sys_group g on g.id=b.group_id

g.game_type=#{gameType} 
and u.u_account_name=#{accountName}
