mapper.select:
SELECT from memberagents
mid = #{mid,jdbcType=INTEGER}
order by id

SELECT a.mid,b.*,FROM_UNIXTIME(a.mktime,'%Y-%m-%d %H:%i:%S') as comeTime, a.sitemid as asitemid,b.sitemid as bsitemid,a.realname AS truename FROM memberagents as a left join common_agents_info as b ON a.sitemid=b.sitemid WHERE a.alevel=1 AND a.belongid=#{belongid,jdbcType=INTEGER}
and a.mid = #{mid,jdbcType=INTEGER}
ORDER BY a.mktime DESC


SELECT COUNT(*) AS total
FROM memberagents
WHERE firstmid= #{firstmid,jdbcType=INTEGER}
      AND belongid= #{belongid,jdbcType=INTEGER}
      AND alevel>1

SELECT a.mid,b.*,a.realname AS truename ,FROM_UNIXTIME(a.mktime,'%Y-%m-%d %H:%i:%S') as comeTime

FROM memberagents as a
left join common_agents_info as b
ON a.sitemid=b.sitemid
WHERE a.firstmid= #{firstmid,jdbcType=INTEGER}
AND a.belongid= #{belongid,jdbcType=INTEGER}
AND a.alevel>1

  and a.mid = #{mid,jdbcType=INTEGER}

ORDER BY a.mktime
DESC
