mapper.select:
	SELECT     m.mid, m.name, m.sex, m.btd, m.city, icon, invite, gp, sta,FROM_UNIXTIME(mtime, '%Y-%m-%d %H:%m:%s') AS mtime , email, bindtime, tel, realname, 
    identity, yellowvip, isyearvip, passwd, salt, lgtm, lxlg, qq, address,g.gold from memberfides0 m
	INNER JOIN membercommongame0 g
	on  m.mid=g.mid


	AND g.gold >= #{gold,jdbcType=INTEGER}


	m.name = #{realname}


	AND m.mid = #{mid}



	AND from_unixtime(m.mtime,'%Y-%m-%d') >= #{startTime}


	AND from_unixtime(m.mtime,'%Y-%m-%d') <= #{endTime}



	order by m.mid desc

SELECT mf.name,mf.sex,mf.btd as born ,mf.city,mf.icon, mf.invite,mf.gp AS gameArea,mf.sta as status, FROM_UNIXTIME(mf.mtime,'%Y-%m-%d %H:%i:%S') as mtime, mf.email,FROM_UNIXTIME(mf.bindTime,'%Y-%m-%d %H:%i:%S') as bindTime,mf.tel,mf.mid, ma.sitemid,mf.identity idCard,mf.yellowvip,mf.isyearvip, mf.passwd as sbId,ma.alevel,FROM_UNIXTIME(ma.mktime,'%Y-%m-%d %H:%i:%S') as mktime,ma.belongid, mf.active_assets AS comeFrom,mcg.gold,mcg.vip,mf.lxlg as totalLgin, FROM_UNIXTIME(mf.lgtm,'%Y-%m-%d %H:%i:%S') as lastLogin,mcg.bankpasswd,mcg.bankAssets,mf.address, mf.qq from memberfides0 mf LEFT JOIN memberagents ma ON ma.mid = mf.mid left JOIN membercommongame0 mcg ON mcg.mid = mf.mid
and mf.mid = #{mid,jdbcType=INTEGER}
and ma.sitemid = #{sitemid,jdbcType=VARCHAR}
mf.mid = #{mid,jdbcType=INTEGER} and ma.sitemid = #{sitemid,jdbcType=VARCHAR}

SELECT
mf.mid, name, sex, btd, city, icon, invite, gp, sta, mtime, email, bindtime, tel, realname,
identity, yellowvip, isyearvip, passwd, salt, lgtm, lxlg, qq, address, regip, active_assets
FROM memberfides0 mf
LEFT JOIN members mb
ON mf.mid = mb.mid


    and mf.mid = #{mid,jdbcType=INTEGER}


    and mf.name = #{name,jdbcType=VARCHAR}


    and mb.sitemid = #{sitemid,jdbcType=VARCHAR}




        mf.mid = #{mid,jdbcType=INTEGER}
        and mf.name = #{name,jdbcType=VARCHAR}
        and mb.sitemid = #{sitemid,jdbcType=VARCHAR}




order by mf.mid
